// import axios from "axios";
// import $ from "jquery";
import React, { Component } from "react";
// import DeleteBtn from "../../components/DeleteBtn";
import API from "../../utils/API";
// import { Link } from "react-router-dom";
import { Col, Row, Container } from "../../components/Grid";
import {SavedRecipesCard} from "../../components/Card";
// import { Input, TextArea, FormBtn } from "../../Components/Form";
import { List, ListItem } from "../../components/List";
import { ViewBtn, DeleteBtn, ViewMoreBtn } from "../../components/Buttons"


class Saved extends Component {
  constructor(props) {
    super(props)

    this.handleShowMore = this.handleShowMore.bind(this);

    // state
    this.state = {
      recipes: [],
      showItems: 3
    };

  }

  handleShowMore() {
    this.setState({
      showItems: 
        this.state.showItems >= this.state.recipes.length ?
          this.state.showItems : this.state.showItems + 1
    })
  }

  // componentDidMount
  componentDidMount() {
    console.log("SaveRecipes component mounted");
    this.loadSavedRecipes();
  };

  loadSavedRecipes = () => {
    console.log("loading saved recipes")
    API.getSavedRecipes()
    .then(res => {
        console.log(res.data)
        // insert logic here to display 3 only based on time created
        this.setState({ 
          recipes: res.data
        })
      }
    )
    .catch(err => console.log("ERR", err));
  }

  deleteRecipe = id => {
    console.log("...deleting recipe", id);

      API.deleteRecipe(id)
        .then(res => this.loadSavedRecipes())
        .catch(err => console.log(err));
    }


  render() {
    const recipes = this.state.recipes.slice(0, this.state.showItems).map(
      (recipe) => 
        <List>
          <ListItem 
            key={recipe.recipe_id}
            img={recipe.image_url}
            title={recipe.title}>
            <DeleteBtn 
            onClick={() => this.deleteRecipe(recipe._id)}/>
            <ViewBtn link={recipe.source_url} />
          </ListItem>
      </List>

    )
    return (
      <Container fluid>
      <Row>
        <Col size="lg-12">
        <Row>
          <Col size="llg-12">
          {this.state.recipes.length ? (
            <SavedRecipesCard>
              {recipes}
              <ViewMoreBtn onClick={this.handleShowMore}/>
            </SavedRecipesCard>
          ) : (
            <h4>No saved recipes</h4>
          )}
          </Col>
        </Row>
        </Col>
      </Row>

      </Container>
    );
  }
}

export default Saved;
