// import axios from "axios";
// import $ from "jquery";
import React, { Component } from "react";
// import DeleteBtn from "../../components/DeleteBtn";
import API from "../../utils/API";
// import { Link } from "react-router-dom";
import { Col, Row, Container } from "../../components/Grid";
import {SavedRecipesCard} from "../../components/Card";
// import { Input, TextArea, FormBtn } from "../../Components/Form";
import { List, ListItem } from "../../components/List";
import { ViewBtn, DeleteBtn } from "../../components/Buttons"


class Saved extends Component {

  // state
  state = {
    recipes: [],
  };

  // componentDidMount
  componentDidMount() {
    console.log("SaveRecipes component mounted");
    this.loadSavedRecipes();
  };

  loadSavedRecipes = () => {
    console.log("loading saved recipes")
    API.getSavedRecipes()
    .then(res => {
        console.log(res.data)
        const maxNumberRecipes = 3;
        for (var i = 0; i < maxNumberRecipes.length; i++) {
        // insert logic here to display 3 only based on time created
        this.setState({ 
          recipes: res.data
        })
      }
    }
    )
    .catch(err => console.log("ERR", err));
  }

  deleteRecipe = id => {
    console.log("...deleting recipe", id);

      API.deleteRecipe(id)
        .then(res => this.loadSavedRecipes())
        .catch(err => console.log(err));
    }


  render() {
    return (
      <Container fluid>
      <Row>
        <Col size="md-9">
        <Row>
          <Col size="lg-10 md-6 sm-12">
            <SavedRecipesCard>
              {this.state.recipes.length ? (
                <List>
                  {this.state.recipes.map(recipe => (
                    <ListItem 
                      key={recipe.recipe_id}
                      img={recipe.image_url}
                      title={recipe.title}>
                      <DeleteBtn 
                      onClick={() => this.deleteRecipe(recipe._id)}/>
                      <ViewBtn link={recipe.source_url} />
                    </ListItem>
                  ))}
                </List>
              ) : (
                  <h4>No results to display</h4>
                )}
            </SavedRecipesCard>
          </Col>
        </Row>
        </Col>
      </Row>

      </Container>
    );
  }
}

export default Saved;
