//         .catch(err => console.log(err));
//     }

  render() {
    const recipes = this.state.recipes.map(
      (recipe) => 
        <ul>
            <li>
                {recipe}
            </li>
        </ul>
    )
    return (
      <Container fluid>
        {recipes}  
      </Container>
    );

  }
}

export default Saved;
