import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Login.css';

class Register extends Component {

  constructor() {
    super();
    this.state = {
      name: '',
      lastName: '',
      email: '',
      password: ''
    };
  }
  onChange = (e) => {
    const state = this.state
    state[e.target.name] = e.target.value;
    this.setState(state);
  }

  handleRegister = (e) => {
    console.log("register event", e)
    e.preventDefault();

    const { name, lastName, email, password } = this.state;

    axios.post('/api/auth/register', { name, lastName, email, password })
      .then((result) => {
        console.log(result);
        this.props.history.push("/login")
      })
      .catch(err);
  }

  render() {
    const { name, lastName, email, password } = this.state;
    return (
      <div class="container">
        <h1>Yum(Me)</h1>
        <form class="form-signin" onSubmit={this.onSubmit}>
          <h2 class="form-signin-heading">Register</h2>
          <label for="inputName" class="sr-only">Name</label>
          <input type="name" class="form-control" placeholder="Name" name="name" 
          value={name} onChange={this.onChange} required/>
          <label for="inputLastName" class="sr-only">Last Name</label>
          <input type="lastName" class="form-control" placeholder="Lastname" name="lastName" 
          value={lastName} onChange={this.onChange} required/>
          <label for="inputEmail" class="sr-only">Email address</label>
          <input type="email" class="form-control" placeholder="Email address" name="email" value={email} onChange={this.onChange} required/>
          <label for="inputPassword" class="sr-only">Password</label>
          <input type="password" class="form-control" placeholder="Password" name="password" value={password} onChange={this.onChange} required/>
          <button class="btn btn-lg btn-primary btn-block" type="submit" onClick={this.handleRegister}>Register</button>
        </form>
      </div>
    );
  }
}

export default Register;
